import { createContext, useContext, useReducer } from "react";

export const useUserStore = () => useContext(UserStore);

export const UserStore = createContext();

const initialstate = {
  users: [{ id: 1, name: "홍길동", nickname: "히히" }],
};

const userReducer = (state, action) => {
  switch (action.type) {
    case "ADD_USER":
      return {
        ...state,
        users: [...state.users, action.payload],
      };
    // case "NEWUSER_ADD_ISEDIT":
    //   return {
    //     action.payload로 추가된 newUser에만 isEdit을 true로 넣어줌
    //   };
    // case "RESET_USER":
    //   return {
    //     users: [...state.users] 초기 상태로 돌아감
    //   }
    // case "SUBMIT_USER":
    //   return {
    //     const newUserState = state.users.filter(user => user.isEdit === true)
    //     console.log(newUserState)
    //   }
    default:
      throw new Error("오류오류");
  }
};

const UserStoreProvider = ({ children }) => {
  const [users, userDispatch] = useReducer(userReducer, initialstate);
  return (
    <UserStoreProvider value={[users, userDispatch]}>
      {children}
    </UserStoreProvider>
  );
};

export default UserStoreProvider;
