import { useContext, useState } from "react";
import { ModalContext } from "../../../../../store/2_context";

const ContextQ1Detail2 = () => {
  const modalContext = useContext(ModalContext);
  const [btn1State, setBtn1State] = useState(false);
  const { isModalOpen, setIsModalOpen } = modalContext;

  const handleModalOpenToggle = () => {
    if (btn1State) {
      setIsModalOpen(!isModalOpen);
    }
  };

  return (
    <>
      <h2>ContextQ1Detail2</h2>
      <button onClick={handleModalOpenToggle}>
        {isModalOpen ? "숨기기" : "보이기"}
      </button>
    </>
  );
};
export default ContextQ1Detail2;
